import React, { useState, FormEvent } from 'react'
import {Form,Button,Checkbox, Container, Input,Image} from "semantic-ui-react";

import RegisterLogo from "../assets/register-logo.png";

interface RegisterProps {

}

export const Register: React.FC<RegisterProps> = ({}) => {

    const [email,setEmail] = useState("");
    const [username,setUsername]= useState("");
    const [password,setPassword] = useState("");

    const onSubmit = (e: FormEvent)=>{
        e.preventDefault();
        console.log(email,username,password);

    }
        return (
            <Container text>

                <Image src="/home/zied/project/js/slack-clone/client/src/assets/register-logo.png" size='small' />
      <Form onSubmit={(e)=> onSubmit(e)}>
    <Form.Field>
      <label>Username</label>
      <Input onChange={(e)=> setUsername(e.target.value)} placeholder='username'  fluid />
    </Form.Field>

    <Form.Field>
      <label>Email</label>
      <Input  onChange={(e)=> setEmail(e.target.value)}placeholder='email' fluid />
    </Form.Field>
    <Form.Field>
      <label>Password</label>
      <Input type="password" onChange={(e)=> setPassword(e.target.value)} placeholder='password' fluid />
    </Form.Field>
    <Form.Field>
      <Checkbox label='I agree to the Terms and Conditions' />
    </Form.Field>
    <Button  type='submit'>Submit</Button>
  </Form>
  </Container>

        );
}
